import React, { useState } from "react";
import "../assets/scss/LoginForm.css";

const Login = () => {
  const [isRegistering, setIsRegistering] = useState(false); // Quản lý trạng thái đăng nhập/đăng ký
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = () => {
    console.log("Login clicked");
    // Handle login logic here
  };

  const handleRegister = () => {
    console.log("Register clicked");
    // Handle register logic here
  };

  const handleForgotPassword = () => {
    console.log("Forgot password clicked");
    // Handle forgot password logic
  };

  const toggleRegister = () => {
    setIsRegistering(!isRegistering);
  };

  return (
    <div className="login-container">
      <h2>{isRegistering ? "Đăng ký tài khoản" : "Xin chào! Bắt đầu nào"}</h2>
      <p>
        {isRegistering ? "Tạo tài khoản để tiếp tục" : "Đăng nhập để tiếp tục"}
      </p>
      <input
        type="text"
        placeholder="Username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button
        className="login-button"
        onClick={isRegistering ? handleRegister : handleLogin}
      >
        {isRegistering ? "ĐĂNG KÝ" : "ĐĂNG NHẬP"}
      </button>

      {!isRegistering && (
        <div className="extra-options">
          <p className="forgot-password" onClick={handleForgotPassword}>
            Forgot password?
          </p>
          <span> | </span>
          <p className="register-link" onClick={toggleRegister}>
            Đăng ký tài khoản
          </p>
        </div>
      )}

      {isRegistering && (
        <p className="back-to-login" onClick={toggleRegister}>
          Quay lại đăng nhập
        </p>
      )}

      <button className="home-button">TRANG CHỦ</button>
    </div>
  );
};

export default Login;
